import React, { useEffect, useState } from 'react';
import { getPlaces } from '../API/placesaction';
import '../styles'
const Places = ({ searchTerm }) => {
    const [places, setPlaces] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        getPlaces()
            .then(data => setPlaces(data))
            .catch(err => setError(err));
    }, []);

    if (error) {
        return <div>Error: {error.message}</div>;
    }

    return (
        <div>
            <div className="container">
                <div className="row">
                    {
                        places.filter((place) => {
                            if(searchTerm === ""){
                                return place;
                            } else if(place.name.toLowerCase().includes(searchTerm.toLowerCase())){
                                return place;
                            }
                            return null;
                        }).map((place) => {
                            return (
                                <div className="col-md-4" key={place.id}>
                                    <img src={place.image} alt={place.name} style={{ width: '100%', height: 'auto' }}/>
                                    <div className='card-body'>
                                        <h5 className='card-title' style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>{place.name}</h5>
                                        <p className="card-text">{place.description}</p>
                                        <a href={`/book/${place.id}`} className="btn btn-primary" style={{ display: 'inline-block', padding: '0.5rem 1rem', backgroundColor: 'blue', color: 'white', textDecoration: 'none' }}>Go Book</a>
                                    </div>
                                </div>
                            );
                        })
                    }
                </div>
            </div>
        </div>
    );
};

export default Places;
