import React, { useState } from 'react';
import axios from 'axios';

const Booking = () => {
  const [formData, setFormData] = useState({
    email: '',
    startDate: '',
    endDate: '',
    mobileNumber: '',
    name: '',
    numberOfPeople: '',
  });

  const { email, startDate, endDate, mobileNumber, name, numberOfPeople } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:5000/api/book/', formData);

      alert('Booking submitted successfully');

      setFormData({
        email: '',
        startDate: '',
        endDate: '',
        mobileNumber: '',
        name: '',
        numberOfPeople: '',
      });
    } catch (error) {
      console.error('Error submitting booking:', error);
      alert('Failed to submit booking');
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label">
                Email address
              </label>
              <input
                type="email"
                className="form-control"
                name="email"
                value={email}
                onChange={handleChange}
                aria-describedby="emailHelp"
              />
              <div id="emailHelp" className="form-text">
                We'll never share your email with anyone else.
              </div>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputStartDate" className="form-label">
                Start Date
              </label>
              <input
                type="date"
                className="form-control"
                name="startDate"
                value={startDate}
                onChange={handleChange}
                aria-describedby="emailHelp"
              />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEndDate" className="form-label">
                End Date
              </label>
              <input
                type="date"
                className="form-control"
                name="endDate"
                value={endDate}
                onChange={handleChange}
                aria-describedby="emailHelp"
              />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputMobileNumber" className="form-label">
                Mobile Number
              </label>
              <input
                type="number"
                className="form-control"
                name="mobileNumber"
                value={mobileNumber}
                onChange={handleChange}
                aria-describedby="emailHelp"
              />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputName" className="form-label">
                Name
              </label>
              <input
                type="text"
                className="form-control"
                name="name"
                value={name}
                onChange={handleChange}
                aria-describedby="emailHelp"
              />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputNumberOfPeople" className="form-label">
                No of People
              </label>
              <input
                type="number"
                className="form-control"
                name="numberOfPeople"
                value={numberOfPeople}
                onChange={handleChange}
                aria-describedby="emailHelp"
              />
              
            </div>
            <button type="submit" className="btn btn-primary">
              
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Booking;
