import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { getPlaces } from '../API/placesaction';
import { getProfile } from '../API/profile';
import '../styles/Navbars.css';

function Navbars({ setSearchTerm }) {
  const [places, setPlaces] = useState([]);
  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false); 
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    getPlaces()
      .then(data => {
        setPlaces(data);
      })
      .catch(err => setError(err));

    // Replace this with actual logic to check if the user is logged in
    const userLoggedIn = true; // Placeholder, set this based on actual login status
    setIsLoggedIn(userLoggedIn);

    if (userLoggedIn) {
      getProfile()
        .then(profileData => {
          setProfile(profileData);
        })
        .catch(err => setError(err));
    }
  }, []);

  const handleSearch = e => {
    const term = e.target.value;
    setSearchTerm(term);
  };

  return (
    <Navbar expand="lg" className="bg-body-tertiary">
      <Container fluid>
        <Navbar.Brand href="#">BOOK ANYWHERE</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav className="me-auto my-2 my-lg-0" style={{ maxHeight: '100px' }} navbarScroll>
            <Nav.Link href="#action1">Home</Nav.Link>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
              onChange={handleSearch}
            />
            <Button variant="outline-success">Search</Button>
          </Form>
          <Nav>
            {isLoggedIn && profile ? (
              <Nav.Link as={Link} to="/profile">
                <img
                  src={profile.img} // Assuming profile.img is the URL to the profile image
                  alt="Profile"
                  className="profile-logo"
                  style={{ width: '30px', height: '30px', borderRadius: '50%' }}
                />
              </Nav.Link>
            ) : (
              <>
                <Nav.Link as={Link} to="/login">Login</Nav.Link>
                <Nav.Link as={Link} to="/signup">Signup</Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navbars;
