import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { getPlaces } from '../API/placesaction';
import { getProfile } from '../API/profile';
import img from "../../src/assets/Img/profile_logo.png";
import '../styles/Navbars.css';

function Navbars({ setSearchTerm }) {
  const [places, setPlaces] = useState([]);
  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [profile, setProfile] = useState(null);

  
  useEffect(() => {
    getPlaces()
      .then(data => {
        setPlaces(data);
      })
      .catch(err => setError(err));

      const token = localStorage.getItem('authToken'); // Replace with your token key
    const userLoggedIn = !!token; // Check if token exists
    setIsLoggedIn(userLoggedIn);
     if (userLoggedIn) {
      const userId = 'user-id-from-token-or-context'; // Replace with actual user ID retrieval method
      fetch(`/api/profile/${userId}`, {
        headers: {
          'Authorization': `Bearer ${token}`, // If you're using Bearer tokens
        },
      })
        .then(response => response.json())
        .then(data => {
          setProfile(data);
        })
        .catch(err => console.error(err));
    }
  
  }, []);

  

  const handleSearch = e => {
    const term = e.target.value;
    setSearchTerm(term);
  };

  return (
    <Navbar expand="lg" className="bg-body-tertiary">
      <Container fluid>
        <Navbar.Brand href="#">BOOK ANYWHERE</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav className="me-auto my-2 my-lg-0" style={{ maxHeight: '100px' }} navbarScroll>
            <Nav.Link href="#action1">Home</Nav.Link>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
              onChange={handleSearch}
            />
            <Button variant="outline-success">Search</Button>
          </Form>
          <Nav>
            <Nav.Link href={`/login`}>{isLoggedIn ? "Login":<img width={"50px"}src={img}/>}</Nav.Link>
            <Nav.Link href={`/signup`}>{isLoggedIn ? "Signup":""}</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navbars;
