import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/login', {
        email,
        password
      });

      setEmail('');
      setPassword('');
      alert('Logged in successfully');

      window.location.href = '/';
    } catch (error) {
      console.error('Error submitting:', error);
      alert('Failed to login');
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center" style={{ minHeight: '100vh'  }}>
      <div className="row">
        <div className="col-md-8 border p-4" style={{ width: '500px', height: '400px'}}> {/* Adjust width and height */}
          <form onSubmit={handleSubmit} className="d-flex flex-column h-100">
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label" style={{fontSmooth}}>Email address</label>
              <input type="email" className="form-control" id="exampleInputEmail1" value={email} onChange={(e) => setEmail(e.target.value)} aria-describedby="emailHelp" />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
              <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} id="exampleInputPassword1" />
            </div>
            <button type="submit" className="btn btn-primary mt-auto">Login</button> {/* Use mt-auto to push button to the bottom */}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
