import React from 'react';
import axios from 'axios';

const Booking = () => {
  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent the default form submission

    // Get form data
    const formData = new FormData(event.target);

    try {
      const response = await axios.post('http://localhost:5000/api/book/', formData);
      console.log(response.data); // Log the response data if needed
    } catch (error) {
      console.error(error); // Log any errors
    }
  };

  return (
    <div>
      {/* Provide the booking form */}
      <div className="container">
        <div className="row">
          <div className="col-md-6">
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                <input type="email" className="form-control" id="exampleInputEmail1" name="email" aria-describedby="emailHelp" required/>
                <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
              </div>
              {/* Add start date, end date, mobile number, name, number of people */}
              {/* Include other input fields similar to the above with appropriate labels, types, and names */}
              <div className="mb-3">
                <label htmlFor="idProof">ID Proof</label>
                <input type="file" className="form-control" id="idProof" name="idproof" aria-describedby="idProofHelp" required/>
              </div>
              <div className="mb-3">
                <label htmlFor="addressProof">Address Proof</label>
                <input type="file" className="form-control" id="addressProof" name="addressproof" aria-describedby="addressProofHelp" required/>
              </div>
              <button type="submit" className="btn btn-primary">Pay Now</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Booking;
