import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { getPlaces } from '../API/placesaction';
import '../styles/Navbars.css';

function Navbars({ setSearchTerm }) {
  const [places, setPlaces] = useState([]);
  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track if user is logged in

  useEffect(() => {
    getPlaces()
      .then(data => {
        setPlaces(data);
      })
      .catch(err => setError(err));

    // Check if user is logged in (this is just a placeholder logic)
    const userLoggedIn = false; // Replace with actual login check
    setIsLoggedIn(userLoggedIn);
  }, []);

  const handleSearch = e => {
    const term = e.target.value;
    setSearchTerm(term);
  };

  return (
    <Navbar expand="lg" className="bg-body-tertiary">
      <Container fluid>
        <Navbar.Brand href="#">BOOK ANYWHERE</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav className="me-auto my-2 my-lg-0" style={{ maxHeight: '100px' }} navbarScroll>
            <Nav.Link href="#action1">Home</Nav.Link>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
              onChange={handleSearch}
            />
            <Button variant="outline-success">Search</Button>
          </Form>
          <Nav>
            {isLoggedIn ? (
              <Nav.Link as={Link} to="/profile">
                <img
                  src="/path-to-profile-logo.png" 
                  alt="Profile"
                  className="profile-logo"
                  style={{ width: '30px', height: '30px', borderRadius: '50%' }}
                />
              </Nav.Link>
            ) : (
              <>
                <Nav.Link as={Link} to="/login">Login</Nav.Link>
                <Nav.Link as={Link} to="/signup">Signup</Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navbars;
